  public void testShapePS() {
    final Shape shape = new Cartouche();
    shape.setBorderTincture(Tincture.ARGENT);
    shape.setFieldTincture(Tincture.AZURE);
    shape.setOrdinary(Ordinary.ANNULET);
    shape.setOrdinaryTincture(Tincture.GULES);
    assertEquals(Tincture.ARGENT, shape.getBorderTincture());
    assertEquals(Tincture.AZURE, shape.getFieldTincture());
    assertEquals(Ordinary.ANNULET, shape.getOrdinary());
    assertEquals(Tincture.GULES, shape.getOrdinaryTincture());
    boolean fail = false;
    final ByteArrayOutputStream bos = new ByteArrayOutputStream();
    for (final Ordinary ord : Ordinary.values()) {
      bos.reset();
      shape.setOrdinary(ord);
      try (final PrintStream out = new PrintStream(bos)) {
        shape.renderPostScript(out);
      }
      final String out = bos.toString().replace('\n', '#');
      final int k = ord.ordinal();
      if (!out.equals(k < EXPECTED_PS.length ? EXPECTED_PS[ord.ordinal()] : "")) {
        fail = true;
        System.out.println('"' + out + "\", // " + ord);
      }
    }
    if (fail) {
      fail();
    }
  }

